document.getElementById("list-card").addEventListener("wheel",function(e){if(e.deltaMode==e.DOM_DELTA_PIXEL)var t=1;else if(e.deltaMode==e.DOM_DELTA_LINE)t=parseInt(getComputedStyle(this).lineHeight);else if(e.deltaMode==e.DOM_DELTA_PAGE)t=this.clientHeight;0!=e.deltaY&&(this.scrollLeft+=t*e.deltaY,e.preventDefault())}),document.getElementById("reviews__scroll").addEventListener("click",function(e){const t=document.getElementById("card-"+e.target.id);null!==t&&document.getElementById("list-card").scrollTo(t.offsetLeft-t.parentElement.offsetLeft,0)});